export default interface Options {
  /**
   * By default, models run in full precision. Optionally run the model quantized, in either 8-bit or 4-bit by passing `8` or `4`. Currently disabled. Coming soon
   */
  precision?: number;
  /**
   * Number of serverless instances. For example, setting to `3` allows you to make 3 parallel inferences, while setting to `1` means you can do 1 inference in any given moment.
   * Default: `1`
   */
  concurrency?: number;
  /**
   * How much time to wait before the instances automatically shuts down. By default, if an instance does not get a request in `300` seconds, then it shuts down.
   * Default: `300`
   */
  timeout?: number;
}
